const express = require('express')
const app = express()

const studentRouter = require('./routes/students')

const PORT = 8080

app.get('/about/students/', (req, res) => {
  const students = req.params.students
  res.send(`Here is information about all the students ${students}!`)
})

app.get('/about/students/:studentId', (req, res) => {
  const studentId = req.params.studentId
  res.send(`Here is information about student ${studentId}!`)
})

app.listen(PORT)

module.exports = app
